/* Formatted on 12/24/2019 4:25:05 PM (QP5 v5.256.13226.35510) */ SELECT DISTINCT NVL ( CASE WHEN (SELECT HAOU.TYPE FROM HR_ALL_ORGANIZATION_UNITS HAOU WHERE HAOU.ORGANIZATION_ID = PAAF.ORGANIZATION_ID) = 'BD_MJ_HR_ORG_CO' THEN (SELECT HAOU.NAME FROM HR_ALL_ORGANIZATION_UNITS HAOU WHERE HAOU.ORGANIZATION_ID = PAAF.ORGANIZATION_ID) ELSE ( SELECT HAOU.NAME FROM PER_ORG_STRUCTURE_ELEMENTS POSE, HR_ALL_ORGANIZATION_UNITS HAOU WHERE POSE.ORGANIZATION_ID_PARENT = HAOU.ORGANIZATION_ID AND HAOU.TYPE = 'BD_MJ_HR_ORG_CO' START WITH POSE.ORGANIZATION_ID_CHILD = PAAF.ORGANIZATION_ID CONNECT BY PRIOR POSE.ORGANIZATION_ID_PARENT = POSE.ORGANIZATION_ID_CHILD) END, CASE WHEN (SELECT HAOU.TYPE FROM HR_ALL_ORGANIZATION_UNITS HAOU WHERE HAOU.ORGANIZATION_ID = PAAF.ORGANIZATION_ID) = 'BD_MJ_HR_ORG_BD' THEN (SELECT HAOU.NAME FROM HR_ALL_ORGANIZATION_UNITS HAOU WHERE HAOU.ORGANIZATION_ID = PAAF.ORGANIZATION_ID) ELSE ( SELECT HAOU.NAME FROM PER_ORG_STRUCTURE_ELEMENTS POSE, HR_ALL_ORGANIZATION_UNITS HAOU WHERE POSE.ORGANIZATION_ID_PARENT = HAOU.ORGANIZATION_ID AND HAOU.TYPE = 'BD_MJ_HR_ORG_BD' START WITH POSE.ORGANIZATION_ID_CHILD = PAAF.ORGANIZATION_ID CONNECT BY PRIOR POSE.ORGANIZATION_ID_PARENT = POSE.ORGANIZATION_ID_CHILD) END) COMPANY, -- PAPF.ORIGINAL_DATE_OF_HIRE JOIN_DATE, -- PAPF.DATE_OF_BIRTH DATE_OF_BIRTH, -- ppf.payroll_name payroll, -- TO_NUMBER (PAPF.EMPLOYEE_NUMBER) EMPLOYEE_NUMBER, -- PAPF.FULL_NAME employee_name, -- (SELECT DISTINCT PPT.SEGMENT1 || '-' || PPT.SEGMENT2 -- FROM per_all_positions pep, PER_POSITION_DEFINITIONS PPT -- WHERE pep.POSITION_DEFINITION_ID = PPT.POSITION_DEFINITION_ID -- AND PEP.POSITION_ID = PAAF.POSITION_ID -- AND PPT.ENABLED_FLAG = 'Y') -- DESIGNATION,
PCAL.SEGMENT3 COST_CENTER, FV.DESCRIPTION COST_CENTER_NAME --haou.name department FROM per_all_people_f papf, per_all_assignments_f paaf, hr_all_organization_units haou, pay_payrolls_f ppf, PAY_COST_ALLOCATION_KEYFLEX PCAL, FND_FLEX_VALUES_VL FV WHERE PAPF.PERSON_ID = PAAF.PERSON_ID AND PAAF.ORGANIZATION_ID = haou.ORGANIZATION_ID AND PAAF.PAYROLL_ID = PPF.PAYROLL_ID AND SYSDATE BETWEEN PAAF.EFFECTIVE_START_DATE AND PAAF.EFFECTIVE_END_DATE AND SYSDATE BETWEEN PAPF.EFFECTIVE_START_DATE AND PAPF.EFFECTIVE_END_DATE AND HAOU.COST_ALLOCATION_KEYFLEX_ID = PCAL.COST_ALLOCATION_KEYFLEX_ID AND PCAL.SEGMENT3 = fv.flex_value AND FV.FLEX_VALUE_SET_ID = 1014918 and NVL ( CASE WHEN (SELECT HAOU.TYPE FROM HR_ALL_ORGANIZATION_UNITS HAOU WHERE HAOU.ORGANIZATION_ID = PAAF.ORGANIZATION_ID) = 'BD_MJ_HR_ORG_CO' THEN (SELECT HAOU.NAME FROM HR_ALL_ORGANIZATION_UNITS HAOU WHERE HAOU.ORGANIZATION_ID = PAAF.ORGANIZATION_ID) ELSE ( SELECT HAOU.NAME FROM PER_ORG_STRUCTURE_ELEMENTS POSE, HR_ALL_ORGANIZATION_UNITS HAOU WHERE POSE.ORGANIZATION_ID_PARENT = HAOU.ORGANIZATION_ID AND HAOU.TYPE = 'BD_MJ_HR_ORG_CO' START WITH POSE.ORGANIZATION_ID_CHILD = PAAF.ORGANIZATION_ID CONNECT BY PRIOR POSE.ORGANIZATION_ID_PARENT = POSE.ORGANIZATION_ID_CHILD) END, CASE WHEN (SELECT HAOU.TYPE FROM HR_ALL_ORGANIZATION_UNITS HAOU WHERE HAOU.ORGANIZATION_ID = PAAF.ORGANIZATION_ID) = 'BD_MJ_HR_ORG_BD' THEN (SELECT HAOU.NAME FROM HR_ALL_ORGANIZATION_UNITS HAOU WHERE HAOU.ORGANIZATION_ID = PAAF.ORGANIZATION_ID) ELSE ( SELECT HAOU.NAME FROM PER_ORG_STRUCTURE_ELEMENTS POSE, HR_ALL_ORGANIZATION_UNITS HAOU WHERE POSE.ORGANIZATION_ID_PARENT = HAOU.ORGANIZATION_ID AND HAOU.TYPE = 'BD_MJ_HR_ORG_BD' START WITH POSE.ORGANIZATION_ID_CHILD = PAAF.ORGANIZATION_ID CONNECT BY PRIOR POSE.ORGANIZATION_ID_PARENT = POSE.ORGANIZATION_ID_CHILD) END) = :Unit
ORDER BY FV.DESCRIPTION
